
using Bnnsoft.Sdk.Hsm;
using Bnnsoft.Sdk.Signers;
using System;
using System.IO;
namespace CyberClient
{
    static class Program
    {

        public static int TYPE_PDFSIGNATURE_DONTSHOW = 0;
        public static int TYPE_PDFSIGNATURE_TEXT = 1;
        public static int TYPE_PDFSIGNATURE_IMA = 2;
        public static int TYPE_PDFSIGNATURE_TEXTIMA = 3;

        public static String DEFAULT_BASE64_IMAGE = "";


        static void TestSignPdf2()
        {
            byte[] bDataFile = File.ReadAllBytes("sample.pdf");
            var signer = new PDFSigner("2e0e149d973a4735bf8b4aeb7191eb41", "AAprB9Kd8pYlGtIaBnkcaiHVoyFek7fTU4Y0ecVU");
            var signed = signer.Sign(bDataFile,"SHA-1", TYPE_PDFSIGNATURE_TEXTIMA, DEFAULT_BASE64_IMAGE, "hello", Guid.NewGuid().ToString());
            var verify = signer.Verify(signed);
        }


        [STAThread]
        static void Main()
        {
            TestSignPdf2();
        }


    }
}
